import React, { useState } from 'react'
import { Link } from 'react-router-dom';
import axios from 'axios'

const Register = () => {
    const[name, setName] = useState()
    const[email, setEmail]=useState()
    const[password, setPassword]=useState()

const handleSubmit =async (e)=>{
    e.preventDefault();
    try {
        const response = await axios.post('http://localhost:3000/register', { name, email, password });
        console.log(response.data);  // Log the response data for debugging
        // Assuming the backend sends a success message or user object on success
        if (response.data === 'Success') {
            // Handle successful registration (e.g., redirect, show message, etc.)
            console.log('Registration successful!');
        } else {
            console.log('Registration failed');
        }
    } catch (error) {
        console.log('Error during registration:', error);
    }

    
}

  return (
    <div className='d-flex justify-content-center align-items-center bg-secondary vh-100'>
        <div className='bg-white p-3 rounded w-25'>
            <h2>Register</h2>
            <form onSubmit={handleSubmit}>
                <div className='mb-3'>
                    <label htmlFor='email'>
                        <strong>Name</strong>
                    </label>
                    <input type='text' placeholder='Enter Name' autoComplete='off' name='name' className='form-control rounded-0'
                    onChange={(e)=>{setName(e.target.value)}}
                    />
                </div>
                <div className='mb-3'>
                    <label htmlFor='email'>
                        <strong>Email</strong>
                    </label>
                    <input type='text' placeholder='Enter Name' autoComplete='off' name='email' className='form-control rounded-0' onChange={(e)=>{setEmail(e.target.value)}}/>
                </div>
                <div className='mb-3'>
                    <label htmlFor='email'>
                        <strong>Password</strong>
                    </label>
                    <input type='password' placeholder='Enter Name' autoComplete='off' name='password' className='form-control rounded-0' onChange={(e)=>{setPassword(e.target.value)}}/>
                </div>
                <button type='submit' className='btn btn-success w-100 rounded-0'>Register</button>
                </form>
                <p>Already have an account?</p>
                <Link to='/login'  className='btn btn-success w-100 rounded-0'>Login</Link>

            
        </div>
        
    </div>
      
            
  );
}
  

export default Register
